<?php	$foliogine_lite_download_url = foliogine_lite('download_url');	$foliogine_lite_download_text = foliogine_lite('download_text');	$foliogine_lite_download_title = foliogine_lite('download_title');	$foliogine_lite_download_url = foliogine_lite('download_url');	if ( !empty($foliogine_lite_download_url) ):?>		<section class="download">			<div class="container">                <?php					if ( !empty($foliogine_lite_download_text) ):						echo '<a class="text">'.esc_html($foliogine_lite_download_text).'</a>';					endif;					if ( !empty($foliogine_lite_download_title) && !empty($foliogine_lite_download_url) ):						echo '<a href="'.esc_url($foliogine_lite_download_url).'" class="button" title="'.__('Download','foliogine-lite').'">'.esc_html($foliogine_lite_download_title).'</a>';					endif;				?>			</div><!-- .container -->		</section><!-- .download --><?php endif; ?>